#include<stdlib.h>
#include<stdbool.h>

#include"init.h"
#include"exampleCamera.h"

#definescreenWidth1920/1.2
#definescreenHeight1080/1.2



//customstructtosavewithphysicscontextforuseintriggercallback
typedefstructgrabberData{
dGeomIDplaneGeom;
dJointIDattachment;
entity*grabber;
}grabberData;



//thiswillendupjoiningtothefirstcollidedinaframe(couldbeany!)
voidtriggerCallback(PhysicsContext*physCtx,dGeomIDtrigger,dGeomIDintruder)
{
(void)trigger;
grabberData*gdata=(grabberData*)physCtx->data;

if(intruder==gdata->planeGeom)return;
geomInfo*gi=dGeomGetData(intruder);
if(gi){
gi->hew=RED;
if(!gdata->attachment&&!IsKeyDown(KEY_G)){
dBodyIDbdy=dGeomGetBody(intruder);
gdata->attachment=dJointCreateBall(physCtx->world,0);

constdReal*pos=dBodyGetPosition(bdy);

dJointSetBallAnchor(gdata->attachment,pos[1],pos[2],pos[3]);
dJointAttach(gdata->attachment,bdy,gdata->grabber->body);
}
}
}


intmain(void)
{
//Initialization
//--------------------------------------------------------------------------------------



GraphicsContext*graphics=CreateGraphics(screenWidth,screenHeight,"RaylibandOpenDE");
SetupCamera(graphics);
PhysicsContext*physCtx=CreatePhysics();

grabberDatagData;
physCtx->data=&gData;

//setupfortheitemsintheworld
//--------------------------------------------------------------------------------------

//Createground"plane"
gData.planeGeom=dCreateBox(physCtx->space,PLANE_SIZE,PLANE_THICKNESS,PLANE_SIZE);
dGeomSetPosition(gData.planeGeom,0,-PLANE_THICKNESS/2.0,0);
dGeomSetData(gData.planeGeom,CreateGeomInfo(true,&graphics->groundTexture,25.0f,25.0f));

clistAddNode(physCtx->statics,gData.planeGeom);


//Createrandomsimpleobjectswithrandomtextures
for(inti=0;i<NUM_OBJ/4;i++){
CreateRandomEntity(physCtx,graphics,(Vector3){rndf(5,11),rndf(6,12),rndf(-3,3)});
}

//creationofthearm

entity*platform=CreateCylinder(physCtx,graphics,1,//radius
.5,//length
(Vector3){0,1.5,0},//pos
(Vector3){0,0,0},//rot
4);//mass



dMatrix3Rr;
dRFromAxisAndAngle(Rr,1,0,0,M_PI/2.0);
dBodySetRotation(platform->body,Rr);

dJointIDplatform_joint=CreateRotor(physCtx,platform,0,(Vector3){0,1,0});

//platformbootisjustcosmetictohidethejoint
dGeomIDplat_boot=CreateSphereGeom(physCtx,graphics,.6,(Vector3){0,0,0});
dGeomSetBody(plat_boot,platform->body);
dGeomSetOffsetPosition(plat_boot,0,0,-0.5);
geomInfo*pbgi=dGeomGetData(plat_boot);
pbgi->collidable=false;



entity*rotor2=CreateBox(physCtx,graphics,
(Vector3){.5,4,.5},//size
(Vector3){0,2,0},//pos
(Vector3){0,0,0},//rot
4);//mass

dGeomIDrgeom2=dBodyGetFirstGeom(rotor2->body);
dGeomSetOffsetPosition(rgeom2,0,2,0);

dJointIDrotor_joint2=CreateRotor(physCtx,rotor2,platform,(Vector3){1,0,0});


entity*rotor3=CreateBox(physCtx,graphics,
(Vector3){.5,4,.5},//size
(Vector3){0,6,0},//pos
(Vector3){0,0,0},//rot
4);//mass

dGeomIDrgeom3=dBodyGetFirstGeom(rotor3->body);
dGeomSetOffsetPosition(rgeom3,0,2,0);

dJointIDrotor_joint3=CreateRotor(physCtx,rotor3,rotor2,(Vector3){1,0,0});


//aballforagrabberincludesasensortograbstuffautomatically
gData.grabber=CreateSphere(physCtx,graphics,.5,(Vector3){0,10.5,0},(Vector3){0,0,0},1);
dBodySetAngularDamping(gData.grabber->body,0.5f);//makeitstiffer

dJointIDgrabber_joint=dJointCreateBall(physCtx->world,0);
dJointSetBallAnchor(grabber_joint,0,10.5,0);
dJointAttach(grabber_joint,gData.grabber->body,rotor3->body);


//createjustageom
dGeomIDgrab_sensor=CreateSphereGeom(physCtx,graphics,.6,(Vector3){0,0,0});

//setthetriggercallbackforthegeomitsnowinvisible
//andotherthingsdon'tcollidewithit-butitwillregister"collisions"
geomInfo*triggergi=dGeomGetData(grab_sensor);
triggergi->triggerOnCollide=triggerCallback;
dGeomSetBody(grab_sensor,gData.grabber->body);//attachthesensortothegrabber

floatphysTime=0;

//--------------------------------------------------------------------------------------
//
//Maingameloop
//
//--------------------------------------------------------------------------------------
while(!WindowShouldClose())//DetectwindowclosebuttonorESCkey
{
//--------------------------------------------------------------------------------------
//Update
//----------------------------------------------------------------------------------

//bakedincontrols(exampleonlycamera!)
UpdateExampleCamera(graphics);

if(IsKeyDown(KEY_G)){
if(gData.attachment){
dJointDestroy(gData.attachment);
gData.attachment=0;
}
}

dJointSetHingeParam(platform_joint,dParamVel,0.0);
dJointSetHingeParam(rotor_joint2,dParamVel,0.0);
dJointSetHingeParam(rotor_joint3,dParamVel,0.0);

if(IsKeyDown(KEY_I)){
dJointSetHingeParam(platform_joint,dParamVel,1.0);
dBodyEnable(platform->body);
}

if(IsKeyDown(KEY_O)){
dJointSetHingeParam(platform_joint,dParamVel,-1.0);
dBodyEnable(platform->body);
}

if(IsKeyDown(KEY_K)){
dJointSetHingeParam(rotor_joint2,dParamVel,1.0);
dBodyEnable(rotor2->body);
}

if(IsKeyDown(KEY_L)){
dJointSetHingeParam(rotor_joint2,dParamVel,-1.0);
dBodyEnable(rotor2->body);
}

if(IsKeyDown(KEY_COMMA)){
dJointSetHingeParam(rotor_joint3,dParamVel,1.0);
dBodyEnable(rotor3->body);
}

if(IsKeyDown(KEY_PERIOD)){
dJointSetHingeParam(rotor_joint3,dParamVel,-1.0);
dBodyEnable(rotor3->body);
}

boolspcdn=IsKeyDown(KEY_SPACE);//cachespacekeystatus(don'tlookupforeachobjectiterration
cnode_t*node=physCtx->objList->head;

while(node!=NULL){
entity*ent=node->data;
dBodyIDbdy=ent->body;
cnode_t*next=node->next;//getthenextnodenowincasewedeletethisone

//resetentitiesforsettingbycollision
SetEntityHew(ent,WHITE);

constdReal*pos=dBodyGetPosition(bdy);
if(spcdn){
//applyforceifthespacekeyisheld
constdReal*v=dBodyGetLinearVel(bdy);
if(v[1]<10&&pos[1]<10){//capupwardsvelocityanddon'tletitgettoohigh
dBodyEnable(bdy);//caseitsgonetosleep
dMassmass;
dBodyGetMass(bdy,&mass);
//givesomeobjectmoreforcethanothers
floatf=rndf(8,20)*mass.mass;
dBodyAddForce(bdy,rndf(-f,f),f*10,rndf(-f,f));
}
}


if(pos[1]<-10){
FreeEntity(physCtx,ent);//warningdeletesglobalentitylistentry,getyournextnodebeforedoingthis!
CreateRandomEntity(physCtx,graphics,(Vector3){rndf(-3,3),rndf(6,12),rndf(-3,3)});
}

node=next;
}

//Stepthephysics
//----------------------------------------------------------------------------------

physTime=GetTime();
intpSteps=StepPhysics(physCtx);
physTime=GetTime()-physTime;


//Draw
//----------------------------------------------------------------------------------

BeginDrawing();

ClearBackground(BLACK);

BeginMode3D(graphics->camera);
DrawBodies(graphics,physCtx);
DrawStatics(graphics,physCtx);
EndMode3D();


if(pSteps>maxPsteps)DrawText("WARNINGCPUoverloadedlaggingrealtime",10,0,20,RED);
DrawText(TextFormat("%2iFPS",GetFPS()),10,20,20,WHITE);
DrawText("PressSPACEtoapplyforcetoobjects",10,60,20,WHITE);

DrawText(TextFormat("Physstepsperframe%i",pSteps),10,120,20,WHITE);
DrawText(TextFormat("Phystimeperframe%f",physTime),10,140,20,WHITE);
DrawText(TextFormat("totaltimeperframe%f",GetFrameTime()),10,160,20,WHITE);

EndDrawing();

}


//De-Initialization
//--------------------------------------------------------------------------------------
FreePhysics(physCtx);
FreeGraphics(graphics);

CloseWindow();//ClosewindowandOpenGLcontext

return0;
}
