#include<stdlib.h>
#include<math.h>

#include"raylibODE.h"


//instancesslops,levelsectionsandcornersfromanarrayofcoordinates
voidLoadLevel(PhysicsContext*physCtx,GraphicsContext*graphics);

//createsanarrayofmultipistons,andothersupportgeomsto
//provideamarblelift
MultiPiston**CreateLift(PhysicsContext*physCtx,
GraphicsContext*graphics,
Vector3position,
floatyaw,
floatstrength);

#definescreenWidth1920/1.2
#definescreenHeight1080/1.2

constSurfaceTypelevelSurface=SURFACE_WOOD;
#defineMAX_PISTON6

typedefenum{SLOPE,STRAIGHT,CORNER}MeshType;

typedefstructLevelElement{
MeshTypetype;
floatgridX;//Willbemultipliedby2.0
floatgridY;//Willbemultipliedby0.55
floatgridZ;//Willbemultipliedby2.0
floatangle;//Rotationinradians(e.g.,M_PI,-M_PI/2)
}LevelElement;


//Definethelevellayout
LevelElementlevelData[]={
{SLOPE,0,9,0,0.0f},
{STRAIGHT,1,8,0,0.0f},
{SLOPE,2,7,0,0.0f},

{SLOPE,3,6,0,0.0f},
{CORNER,4,5,0,M_PI},
{SLOPE,4,5,1,-M_PI/2},
{CORNER,4,4,2,0.0f},
{STRAIGHT,5,4,2,0.0f},
{CORNER,6,4,2,M_PI/2},
{SLOPE,6,4,1,M_PI/2},
{SLOPE,6,3,0,M_PI/2},
{CORNER,6,2,-1,M_PI},
{CORNER,5,2,-1,-M_PI/2},
{SLOPE,5,2,0,-M_PI/2},
{SLOPE,5,1,1,-M_PI/2},
{SLOPE,5,0,2,-M_PI/2},
{SLOPE,5,-1,3,-M_PI/2},

//after1stlift
{SLOPE,5,6,9,-M_PI/2},
{CORNER,5,5,10,M_PI/2},
{SLOPE,4,5,10,M_PI},
{STRAIGHT,3,4,10,M_PI},
{STRAIGHT,2,4,10,0.0f},
{STRAIGHT,1,4,10,0.0f},
{STRAIGHT,0,4,10,0.0f},
{STRAIGHT,-1,4,10,0.0f},
{CORNER,-2,4,10,0},
{SLOPE,-2,4,9,M_PI/2},
{STRAIGHT,-2,3,8,M_PI/2},
{STRAIGHT,-2,3,7,M_PI/2},
{STRAIGHT,-2,3,6,M_PI/2},
{SLOPE,-2,3,5,M_PI/2},
{STRAIGHT,-2,2,4,M_PI/2},
{STRAIGHT,-2,2,3,M_PI/2},
{STRAIGHT,-2,2,2,M_PI/2},
{SLOPE,-2,2,1,M_PI/2},

//aftersecondlift
{SLOPE,-2,9,-5,M_PI/2},
{STRAIGHT,-2,8,-6,M_PI/2},
{CORNER,-2,8,-7,-M_PI/2},
{STRAIGHT,-1,8,-7,0},
{CORNER,0,8,-7,M_PI},
{SLOPE,0,8,-6,-M_PI/2},

//afterthirdlift
{SLOPE,0,15,0,-M_PI/2},
{STRAIGHT,0,14,1,M_PI/2},
{CORNER,0,14,2,M_PI/2},
{SLOPE,-1,14,2,M_PI},
{CORNER,-2,13,2,-M_PI/2},
{CORNER,-2,13,3,0},
{SLOPE,-1,13,3,0},
{CORNER,0,12,3,M_PI/2},
{CORNER,0,12,2,M_PI},
{SLOPE,-1,12,2,M_PI},
{CORNER,-2,11,2,0},
{SLOPE,-2,11,1,M_PI/2},
{CORNER,-2,10,0,-M_PI/2},
{STRAIGHT,-1,10,0,0},

};




/***********************************************************************
*main!
**********************************************************************/

intmain(void)
{
//init
PhysicsContext*physCtx=CreatePhysics();
GraphicsContext*graphics=CreateGraphics(screenWidth,screenHeight,"RaylibandOpenDESandbox");
SetupCamera(graphics);
graphics->camera.position=(Vector3){0.f,5.f,8.f};
SetCameraYaw(M_PI/4);

constVector3dropPoint=(Vector3){0.f,12.f,0.f};

LoadLevel(physCtx,graphics);



MultiPiston**lift1=CreateLift(physCtx,
graphics,
(Vector3){0.f,0.4f,0.0f},
0,
1000.0f);


MultiPiston**lift2=CreateLift(physCtx,
graphics,
(Vector3){6.f,4.f,8.f},
PI,
1000.0f);

MultiPiston**lift3=CreateLift(physCtx,
graphics,
(Vector3){-10.f,11.f,-18.f},
0,
1000.0f);

intframeCount=0;
intreleased=0;
constintmaxRelesed=16;
constfloatballSize=.6f;

while(!WindowShouldClose())
{
for(inti=0;i<MAX_PISTON;i++){
floatoffset=0.f;
//everyotherpistonis180degreesoutofphase
if(i%2==0)offset=M_PI;
SetMultiPistonVelocity(lift1[i],sin(((float)frameCount)/60.0f+offset));
SetMultiPistonVelocity(lift2[i],sin(((float)frameCount)/60.0f+offset));
SetMultiPistonVelocity(lift3[i],sin(((float)frameCount)/60.0f+offset));
}


if(released<maxRelesed){
if(frameCount%420==0){
entity*e=CreateSphere(physCtx,graphics,ballSize,dropPoint,Vector3Zero(),1);
dBodySetAutoDisableFlag(e->body,0);
geomInfo*gi=dGeomGetData(dBodyGetFirstGeom(e->body));
gi->surface=&gSurfaces[SURFACE_METAL];
released++;
}
}
frameCount++;

UpdateCameraControl(graphics);
StepPhysics(physCtx);

cnode_t*node=physCtx->objList->head;

while(node!=NULL){
entity*ent=node->data;
dBodyIDbdy=ent->body;
cnode_t*next=node->next;//getthenextnodenowincasewedeletethisone
constdReal*pos=dBodyGetPosition(bdy);

if(pos[1]<-10){
FreeEntity(physCtx,ent);//warningdeletesglobalentitylistentry,getyournextnodebeforedoingthis!
//entity*e=CreateSphere(physCtx,graphics,0.55,dropPoint,Vector3Zero(),1);
//dBodySetAutoDisableFlag(e->body,0);
//geomInfo*gi=dGeomGetData(dBodyGetFirstGeom(e->body));
//gi->surface=&gSurfaces[SURFACE_RUBBER];
released--;
}

node=next;
}

//drawing
BeginDrawing();
ClearBackground(BLACK);
BeginMode3D(graphics->camera);
DrawBodies(graphics,physCtx);
DrawStatics(graphics,physCtx);
EndMode3D();

DrawText("Marbles!",10,40,20,RAYWHITE);

EndDrawing();
}

FreePhysics(physCtx);
FreeGraphics(graphics);

//TODOmakethiseasier!
for(inti=0;i<MAX_PISTON;i++)
{
FreeMultiPiston(lift1[i]);
FreeMultiPiston(lift2[i]);
FreeMultiPiston(lift3[i]);
}
free(lift1);
free(lift2);
free(lift3);

CloseWindow();
return0;
}


voidLoadLevel(PhysicsContext*physCtx,GraphicsContext*graphics){
intnumElements=sizeof(levelData)/sizeof(LevelElement);

constfloatlayerHeight=1.17;

for(inti=0;i<numElements;i++){
LevelElemente=levelData[i];
floatea=e.angle+M_PI_2;

switch(e.type)
{
caseSLOPE:
(void)e;//workaroundforc99declafterlabel
floatpAngle=30.0f*(M_PI/180.0f);

if(ea>M_PI*2.0f)ea-=M_PI*2.0f;
dQuaternionq,q2,q3;
dQFromAxisAndAngle(q3,1,0,0,pAngle);
dQFromAxisAndAngle(q2,0,1,0,ea);
dQMultiply0(q,q2,q3);
dMatrix4R;
dQtoR(q,R);

floatoffX=0.5f*cosf(ea);
floatoffZ=0.5f*sinf(ea);

//Rail1
Vector3pos=(Vector3){
(float)e.gridX*2.0f+offX,
(float)e.gridY*layerHeight-.2f,
(float)e.gridZ*2.0f-offZ};
dGeomIDdg=CreateCylinderGeom(physCtx,graphics,0.1f,2.4f,pos);
geomInfo*gi=dGeomGetData(dg);
gi->surface=&gSurfaces[levelSurface];
dGeomSetRotation(dg,R);

//Rail2
pos.x=(float)e.gridX*2.0f-offX;
pos.z=(float)e.gridZ*2.0f+offZ;
dGeomIDdg2=CreateCylinderGeom(physCtx,graphics,0.1f,2.4f,pos);
gi=dGeomGetData(dg2);
gi->surface=&gSurfaces[levelSurface];
dGeomSetRotation(dg2,R);

clistAddNode(physCtx->statics,dg);
clistAddNode(physCtx->statics,dg2);
break;

caseSTRAIGHT:

offX=0.5f*cosf(ea);
offZ=0.5f*sinf(ea);

pos=(Vector3){
(float)e.gridX*2.0f+offX,
(float)e.gridY*layerHeight+0.4f,
(float)e.gridZ*2.0f-offZ};

dg=CreateCylinderGeom(physCtx,graphics,0.1f,2,pos);
gi=dGeomGetData(dg);
gi->surface=&gSurfaces[levelSurface];

pos.x=(float)e.gridX*2.0f-offX;
pos.z=(float)e.gridZ*2.0f+offZ;

dg2=CreateCylinderGeom(physCtx,graphics,0.1f,2,pos);
gi=dGeomGetData(dg2);
gi->surface=&gSurfaces[levelSurface];

//alwaysrotateasZaxiscylindersneedextrahalfpirotation
dQFromAxisAndAngle(q,0,1,0,e.angle+M_PI_2);
dGeomSetQuaternion(dg,q);
dGeomSetQuaternion(dg2,q);

clistAddNode(physCtx->statics,dg);
clistAddNode(physCtx->statics,dg2);
break;

caseCORNER:
//thiswasjustabrainmelt!
offX=(float)e.gridX*2.0f;
offZ=(float)e.gridZ*2.0f;

//Stickwiththerot+M_PI_2thatgaveusconsistency
floatrot=e.angle+M_PI_2;
floats=sinf(rot);
floatc=cosf(rot);

for(intj=0;j<3;j++){
floatpAngle=(j*(M_PI_2/3.0f))+(M_PI_2/6.0f);

floatlx1=-1.0f+0.5f*cosf(pAngle);
floatlz1=-1.0f+0.5f*sinf(pAngle);
floatlx2=-1.0f+1.6f*cosf(pAngle);
floatlz2=-1.0f+1.6f*sinf(pAngle);

pos.x=offX-(lx1*c+lz1*s);
pos.y=(float)e.gridY*layerHeight+0.4f;
pos.z=offZ-(lz1*c-lx1*s);

dQFromAxisAndAngle(q,0,1,0,rot-pAngle);
dQtoR(q,R);

//InnerRail
dg=CreateCylinderGeom(physCtx,graphics,0.1f,0.261f,pos);
gi=dGeomGetData(dg);
gi->surface=&gSurfaces[levelSurface];
dGeomSetRotation(dg,R);
clistAddNode(physCtx->statics,dg);

pos.x=offX-(lx2*c+lz2*s);
pos.y+=0.5f;
pos.z=offZ-(lz2*c-lx2*s);

//OuterRail
dg2=CreateCylinderGeom(physCtx,graphics,0.1f,1.f,pos);
gi=dGeomGetData(dg2);
gi->surface=&gSurfaces[levelSurface];
dGeomSetRotation(dg2,R);
clistAddNode(physCtx->statics,dg2);
}
break;
}

}
}



MultiPiston**CreateLift(PhysicsContext*physCtx,
GraphicsContext*graphics,
Vector3position,
floatyaw,
floatstrength)
{
Vector3upAxis=(Vector3){0.f,1.f,0.f};

//Allocatemultipistonarrayusingrayliballocator
MultiPiston**mp=(MultiPiston**)MemAlloc(sizeof(MultiPiston*)*MAX_PISTON);

dMatrix3R_yaw;
dRFromAxisAndAngle(R_yaw,0,1,0,yaw);

//Pistons
for(inti=0;i<MAX_PISTON;i++)
{
Vector3localPos=(Vector3){10.f,-3.8f+i*1.4f,7.6f+i*1.6f};

Vector3worldPos=Vector3Add(
position,
Vector3RotateByAxisAngle(localPos,upAxis,yaw)
);

Vector3axis=Vector3RotateByAxisAngle(
(Vector3){1.f,0.f,0.f},
upAxis,
yaw
);

mp[i]=CreateMultiPiston(physCtx,graphics,
worldPos,
axis,
3,
2.0f,
1.2f,
strength);

dMatrix3R_local;
dMatrix3R_final;

dRFromEulerAngles(R_local,
0,
M_PI_2,
(-M_PI/16.f)*7.f);

dMultiply0(R_final,R_yaw,R_local,3,3,3);
dBodySetRotation(mp[i]->sections[0]->body,R_final);
PinEntityToWorld(physCtx,mp[i]->sections[0]);
}

//-------------------------------------------------
//LiftEnd
//-------------------------------------------------
{
Vector3localPos=(Vector3){10.f,5.f,16.7f};

Vector3worldPos=Vector3Add(
position,
Vector3RotateByAxisAngle(localPos,upAxis,yaw)
);

dGeomIDliftEnd=CreateBoxGeom(
physCtx,
graphics,
(Vector3){2.f,.1f,4.f},
worldPos
);

dMatrix3R_tilt;
dMatrix3R_final;

dRFromAxisAndAngle(R_tilt,1,0,0,-(M_PI/16.f)*7.f);
dMultiply0(R_final,R_yaw,R_tilt,3,3,3);
dGeomSetRotation(liftEnd,R_final);
clistAddNode(physCtx->statics,liftEnd);
}

//-------------------------------------------------
//Rail1
//-------------------------------------------------
{
Vector3localPos=(Vector3){9.2f,2.f,12.f};
Vector3worldPos=Vector3Add(
position,
Vector3RotateByAxisAngle(localPos,upAxis,yaw)
);

dGeomIDliftRail1=CreateBoxGeom(
physCtx,
graphics,
(Vector3){.1f,.8f,13.f},
worldPos
);

dMatrix3R_tilt;
dMatrix3R_final;

dRFromAxisAndAngle(R_tilt,1,0,0,-(M_PI/16.f)*3.5f);
dMultiply0(R_final,R_yaw,R_tilt,3,3,3);
dGeomSetRotation(liftRail1,R_final);
clistAddNode(physCtx->statics,liftRail1);
}

//-------------------------------------------------
//Rail2
//-------------------------------------------------
{
Vector3localPos=(Vector3){10.8f,2.f,12.f};

Vector3worldPos=Vector3Add(
position,
Vector3RotateByAxisAngle(localPos,upAxis,yaw)
);

dGeomIDliftRail2=CreateBoxGeom(
physCtx,
graphics,
(Vector3){.1f,.8f,13.f},
worldPos
);

dMatrix3R_tilt;
dMatrix3R_final;

dRFromAxisAndAngle(R_tilt,1,0,0,-(M_PI/16.f)*3.5f);

dMultiply0(R_final,R_yaw,R_tilt,3,3,3);

dGeomSetRotation(liftRail2,R_final);

clistAddNode(physCtx->statics,liftRail2);
}

returnmp;
}
