#include"raylibODE.h"


#definescreenWidth1920/1.2
#definescreenHeight1080/1.2



intmain(void)
{
//Initialization
//--------------------------------------------------------------------------------------

//Physicscontext,holdsallphysicsstate
PhysicsContext*physCtx=CreatePhysics();
GraphicsContext*graphics=CreateGraphics(screenWidth,screenHeight,"RaylibandOpenDE");

SetupCamera(graphics);

graphics->camera.position.y=graphics->camera.position.y-7.f;
graphics->camera.position.x=graphics->camera.position.x-16.f;

//setupfortheitemsintheworld
//--------------------------------------------------------------------------------------

//Createground"plane"

TexturedotTex=LoadTexture("data/dot.png");

dGeomIDplaneGeom=dCreateBox(physCtx->space,PLANE_SIZE,PLANE_THICKNESS,PLANE_SIZE);
dGeomSetPosition(planeGeom,0,-PLANE_THICKNESS/2.0,0);
SetGeomOrientationEuler(planeGeom,-M_PI/10.0f,0,0);

geomInfo*groundInfo=CreateGeomInfo(true,&dotTex,25.0f,25.0f);
groundInfo->surface=&gSurfaces[SURFACE_ICE];
dGeomSetData(planeGeom,groundInfo);

clistAddNode(physCtx->statics,planeGeom);


RayCast*redCast=CreateRayCast(12,(Vector3){-5,.5,0},(Vector3){1,0,0},6);
//precalculateendpointaswe'renotmovingtherayaround
//(usedfordrawingthevisual)
Vector3redEnd=Vector3Scale(redCast->direction,redCast->length);
redEnd=Vector3Add(redEnd,redCast->position);

RayCast*greenCast=CreateRayCast(12,(Vector3){1.5,2,-5},(Vector3){-1,0,0},3);
Vector3greenEnd=Vector3Scale(greenCast->direction,greenCast->length);
greenEnd=Vector3Add(greenEnd,greenCast->position);


Quaternionq=QuaternionFromEuler(0,-M_PI*.8,M_PI*.1);
Vector3d=(Vector3){1,0,0};
d=Vector3RotateByQuaternion(d,q);
RayCast*blueCast=CreateRayCast(12,(Vector3){8,1.65,-1.5},d,24);
Vector3blueEnd=Vector3Scale(blueCast->direction,blueCast->length);
blueEnd=Vector3Add(blueEnd,blueCast->position);

floatphysTime=0;
intframeCount=0;
intlastFrameCount=0;
boolpaused=false;
boolstepFrame=false;

intlastBlueCount=0;
intlastRedCount=0;
RayHitlastBlueHit[blueCast->maxHits];
RayHitlastRedHit[redCast->maxHits];

//--------------------------------------------------------------------------------------
//s
//Maingameloop
//
//--------------------------------------------------------------------------------------
while(!WindowShouldClose())//DetectwindowclosebuttonorESCkey
{
//--------------------------------------------------------------------------------------
//Update
//----------------------------------------------------------------------------------

//bakedincontrols(examplecamera)
UpdateCameraControl(graphics);

if(IsKeyPressed(KEY_SPACE)){
paused=!paused;
}
stepFrame=false;
if(IsKeyPressed(KEY_T))stepFrame=true;
cnode_t*node=physCtx->objList->head;



while(node!=NULL){
entity*ent=node->data;
dBodyIDbdy=ent->body;

cnode_t*next=node->next;//getthenextnodenowincasewedeletethisone
constdReal*pos=dBodyGetPosition(bdy);

if(pos[1]<-10){
FreeEntity(physCtx,ent);//warningdeletesglobalentitylistentry,getyournextnodebeforedoingthis!
}

node=next;
}

if(frameCount%60==0&&frameCount!=lastFrameCount){
for(inti=0;i<3;i++){
Vector3pos=(Vector3){-4.0f+i*4.0f,6.5,-7.5};
entity*box=CreateBox(physCtx,graphics,(Vector3){1,1,1},pos,Vector3Zero(),20.f);
SetEntitySurfaces(box,&gSurfaces[SURFACE_ICE]);
lastFrameCount=frameCount;
dGeomIDg=dBodyGetFirstGeom(box->body);
geomInfo*gi=(geomInfo*)dGeomGetData(g);
gi->texture=&dotTex;
}
}

//Stepthephysics
//----------------------------------------------------------------------------------


intpSteps=0;
if(!paused||stepFrame){
physTime=GetTime();
pSteps=StepPhysics(physCtx);
physTime=GetTime()-physTime;
frameCount++;
}


CastRay(physCtx,redCast);
for(inti=0;i<redCast->count;i++){
geomInfo*gi=dGeomGetData(redCast->hits[i].geom);
if(gi)gi->hew=RED;
}
CastRay(physCtx,greenCast);
for(inti=0;i<greenCast->count;i++){
geomInfo*gi=dGeomGetData(greenCast->hits[i].geom);
if(gi)gi->hew=GREEN;
}

CastRay(physCtx,blueCast);
for(inti=0;i<blueCast->count;i++){
geomInfo*gi=dGeomGetData(blueCast->hits[i].geom);
if(gi)gi->hew=BLUE;
}
//Draw
//----------------------------------------------------------------------------------

BeginDrawing();

ClearBackground(BLACK);

BeginMode3D(graphics->camera);
DrawBodies(graphics,physCtx);
DrawStatics(graphics,physCtx);

//raysprobablymoreusefulinvisiblebutinthisexampleweshowthem
DrawLine3D(redCast->position,redEnd,RED);
DrawLine3D(greenCast->position,greenEnd,GREEN);

DrawLine3D(blueCast->position,blueEnd,BLUE);
DrawCube(blueCast->position,.2f,.2f,.2f,WHITE);
DrawCube(blueEnd,.2f,.2f,.2f,RED);

for(inti=0;i<blueCast->count;i++){
DrawCube(blueCast->hits[i].pos,.2f,.2f,.2f,BLUE);
}
for(inti=0;i<greenCast->count;i++){
DrawCube(greenCast->hits[i].pos,.2f,.2f,.2f,GREEN);
}
for(inti=0;i<redCast->count;i++){
DrawCube(redCast->hits[i].pos,.2f,.2f,.2f,RED);
}

EndMode3D();


if(pSteps>maxPsteps)DrawText("WARNINGCPUoverloadedlaggingrealtime",10,0,20,RED);
DrawText(TextFormat("%2iFPS",GetFPS()),10,20,20,WHITE);
DrawText(TextFormat("Physstepsperframe%i",pSteps),10,120,20,WHITE);
DrawText(TextFormat("Phystimeperframe%f",physTime),10,140,20,WHITE);
DrawText(TextFormat("totaltimeperframe%f",GetFrameTime()),10,160,20,WHITE);

//TODOfunction!
for(inti=0;i<blueCast->count;i++){
DrawText(TextFormat("pos:%.2f,%.2f,%.2f,depth:%.2f",
blueCast->hits[i].pos.x,
blueCast->hits[i].pos.y,
blueCast->hits[i].pos.z,
blueCast->hits[i].depth
),30,200+i*30,30,SKYBLUE);
lastBlueHit[i]=blueCast->hits[i];
}

if(blueCast->count>0){
lastBlueCount=blueCast->count;
}else{
for(inti=0;i<lastBlueCount;i++){
DrawText(TextFormat("pos:%.2f,%.2f,%.2f,depth:%.2f",
lastBlueHit[i].pos.x,
lastBlueHit[i].pos.y,
lastBlueHit[i].pos.z,
lastBlueHit[i].depth
),30,200+i*30,30,BLUE);
}
}

for(inti=0;i<redCast->count;i++){
DrawText(TextFormat("pos:%.2f,%.2f,%.2f,depth:%.2f",
redCast->hits[i].pos.x,
redCast->hits[i].pos.y,
redCast->hits[i].pos.z,
redCast->hits[i].depth
),630,200+i*30,30,PINK);
lastRedHit[i]=redCast->hits[i];
}

if(redCast->count>0){
lastRedCount=redCast->count;
}else{
for(inti=0;i<lastRedCount;i++){
DrawText(TextFormat("pos:%.2f,%.2f,%.2f,depth:%.2f",
lastRedHit[i].pos.x,
lastRedHit[i].pos.y,
lastRedHit[i].pos.z,
lastRedHit[i].depth
),630,200+i*30,30,RED);
}
}

EndDrawing();


}

UnloadTexture(dotTex);
free(redCast);
free(greenCast);
free(blueCast);
//De-Initialization
//--------------------------------------------------------------------------------------
FreePhysics(physCtx);
FreeGraphics(graphics);

CloseWindow();//ClosewindowandOpenGLcontext

return0;
}
